import { InferenceClient } from "@huggingface/inference";

const SYSTEM_PROMPT = `You are an assistant that receives a list of ingredients that a user has and suggest a recipe they could make with some or all of those ingredients. You don't need to use every ingredient they mention in your recipe. The recipe can include additional ingredients they didn't mention, but try not to include too many extra ingredients. Format your response in markdown to make it easier to render to a web page.`;

// Alert: Do not publish your project's env keys to any online platforms.
// If you want this published online you will have to create a backend or use some serverless architecture to get around this problem.
// Doing the above will keep your API key private.

export async function getRecipeFromChat(ingredientsArr) {
  try {
    console.log("Sending ingredients to backend:", ingredientsArr); // debug
    const response = await fetch("http://localhost:5000/api/getRecipe", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ ingredients: ingredientsArr })
    });

    if (!response.ok) {
      const errText = await response.text();
      console.error("Backend responded with error:", errText);
      return `Error from backend: ${errText}`;
    }

    const data = await response.json();
    console.log("Backend returned:", data);
    return data.recipe || "No recipe returned";
  } catch (err) {
    console.error("Failed to fetch recipe:", err);
    return "Failed to fetch recipe due to network error";
  }
}
