import { useState } from 'react';
import '../style/list.css';
import { Recipe } from './Recipe';
import { getRecipeFromChat } from "./AI";

export const List = () => {
    // Create state
    const [ingredientList, setIngredientList] = useState([]);

    // Add ingredient handler
    const addIngredient = (formData) => {
        // Assign trimmed value (removed spaces)
        const value = formData.get('ingredient');
        console.log(value);
        // If there is a value update state
        if (value) {
            setIngredientList([...ingredientList, value]);

        }
    };

    // Recipe shown to conditionally render the recipe result
    const [recipeShown, setRecipeShown] = useState(false);

    // Toggle recipe shown
    async function getRecipe() {
        // setRecipeShown(prevShown => !prevShown);
        const recipeMarkdown = await getRecipeFromChat(ingredientList);
        console.log(recipeMarkdown);
    };

    return (
        <>
            <form action={addIngredient}>
                <input name="ingredient" type="text" placeholder='e.g. oregano' aria-label='Add ingredient' />
                <button type="submit">+ Add Ingredient</button>
            </form>

            {ingredientList.length > 0 &&
                <article className='list'>
                    <h2>Ingredients on hand:</h2>
                    <ul>
                        {ingredientList.map((item, index) => (
                            <li key={index}>{item}</li>
                        ))}
                    </ul>
                    {ingredientList.length > 3 &&
                        <div className='get-recipe-container'>
                            <div className='get-recipe-details'>
                                <h3>Ready for a recipe?</h3>
                                <p>Get a recipe from your list of ingredients.</p>
                            </div>
                            <button className='get-recipe-btn' onClick={getRecipe}>Get a recipe</button>
                        </div>}
                </article>}

            {/* Article coming back */}
            {recipeShown && <Recipe />}
        </>
    );
};
